INSERT INTO `tips_news` (`id`,`category`,`content`,`title`,`createdate`,`description`,`uri`) VALUES (9149,'tips','<div class="support-card">\n  <div class="support-grid">\n    <!-- LEFT -->\n    <div class="support-left">\n      <h2>Liên hệ bộ phận hỗ trợ</h2>\n\n      <ol class="support-steps">\n        <li>Nhấn biểu tượng tin nhắn ở góc phải dưới màn hình.</li>\n        <li>Chọn <b>Hỗ trợ người dùng</b>.</li>\n        <li>Nhắn nội dung bạn cần hỗ trợ.</li>\n      </ol>\n\n      <div class="support-note">\n        <span>Ví dụ:</span> Mình không đăng nhập được, nhờ kiểm tra giúp.\n      </div>\n    </div>\n\n    <!-- RIGHT -->\n    <div class="support-right">\n      <img src="https://hust.media/img/icon/hd_support.jpg" alt="Hướng dẫn liên hệ hỗ trợ">\n    </div>\n  </div>\n</div>\n\n<style>\n  .support-card{\n    max-width: 980px;\n    margin: 0 auto;\n    background:#fff;\n    border-radius: 18px;\n    padding: 18px;\n    border: 1px solid rgba(0,0,0,.06);\n    box-shadow: 0 10px 28px rgba(0,0,0,.08);\n    font-family: system-ui, -apple-system, "Segoe UI", Roboto, Arial, sans-serif;\n    color:#1f2937;\n  }\n\n  /* 2 cột */\n  .support-grid{\n    display: grid;\n    grid-template-columns: 1.15fr .85fr; /* trái rộng hơn */\n    gap: 16px;\n    align-items: start;\n  }\n\n  .support-left h2{\n    margin: 0 0 10px;\n    font-size: 28px;\n    font-weight: 900;\n    color:#0f172a;\n    letter-spacing: .2px;\n  }\n\n  .support-steps{\n    margin: 0 0 12px;\n    padding-left: 18px;\n  }\n  .support-steps li{\n    margin: 10px 0;\n    line-height: 1.65;\n    font-size: 18px;\n  }\n\n  .support-note{\n    padding: 14px 14px;\n    border-radius: 14px;\n    background: #eff6ff;\n    border: 1px solid rgba(59,130,246,.25);\n    font-size: 18px;\n    color:#0f172a;\n  }\n  .support-note span{\n    font-weight: 900;\n    color:#2563eb;\n    margin-right: 8px;\n  }\n\n  /* ẢNH: hiển thị đủ chiều dọc (không cắt) */\n  .support-right{\n    border-radius: 14px;\n    border: 1px solid rgba(0,0,0,.06);\n    background: #f8fafc;\n    overflow: hidden;\n\n    display: flex;\n    align-items: center;\n    justify-content: center;\n    padding: 10px; /* tạo viền thoáng */\n  }\n  .support-right img{\n    width: 100%;\n    height: auto;        /* quan trọng */\n    display:block;\n    object-fit: contain; /* không cắt */\n    max-height: 340px;   /* tăng/giảm tuỳ bạn */\n  }\n\n  /* Mobile vẫn chia 2 cột */\n  @media (max-width: 520px){\n    .support-card{ padding: 14px; }\n    .support-grid{\n      grid-template-columns: 1fr 1fr;\n      gap: 12px;\n    }\n    .support-left h2{ font-size: 20px; }\n    .support-steps li{ font-size: 14px; margin: 8px 0; }\n    .support-note{ font-size: 14px; padding: 10px; }\n    .support-right{ padding: 8px; }\n    .support-right img{ max-height: 240px; }\n  }\n</style>\n','Hướng dẫn support','2025-11-23 16:06:51','Hướng dẫn support','tips_supports');
